import React from "react";

const articles = [
  {
    title: "The Harmful Effects of Forcing Children",
    url: "https://www.psychologytoday.com/us/blog/parenting-is-heartwork/202002/why-forcing-children-does-more-harm-good",
  },
  {
    title: "How to Encourage Independence in Children",
    url: "https://www.verywellfamily.com/encouraging-independence-in-children-620484",
  },
  {
    title: "The Importance of Listening to Your Child",
    url: "https://childmind.org/article/the-importance-of-listening-to-your-child/",
  },
  {
    title: "Balancing Guidance and Freedom in Parenting",
    url: "https://www.parents.com/parenting/better-parenting/style/balancing-guidance-and-freedom-in-parenting/",
  },
  {
    title: "Using Technology to Support Learning",
    url: "https://www.commonsensemedia.org/blog/how-to-use-technology-to-support-learning",
  },
];

export default function Articles() {
  return (
    <div className="bg-gray-900 min-h-screen py-12">
      <div className="max-w-7xl mx-auto p-10 bg-gray-800 rounded-3xl shadow-2xl">
        <h1 className="text-4xl font-extrabold mb-10 text-center text-yellow-400 drop-shadow-lg">
          Helpful Articles
        </h1>
        <ul className="space-y-6 list-disc list-inside text-yellow-300 text-lg">
          {articles.map((article, index) => (
            <li key={index}>
              <a
                href={article.url}
                target="_blank"
                rel="noopener noreferrer"
                className="hover:underline"
              >
                {article.title}
              </a>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}
