import React, { useState } from "react";

export default function SpeakOut() {
  const [stories, setStories] = useState([
    {
      name: "Alice",
      message:
        "My parents always wanted me to become a doctor, but I wanted to be an artist. It was tough, but I finally convinced them to support my passion.",
    },
    {
      name: "John",
      message:
        "I was forced to take engineering, but my heart was in music. Speaking out helped me find my own path.",
    },
  ]);

  const [formData, setFormData] = useState({
    name: "",
    message: "",
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (formData.name.trim() && formData.message.trim()) {
      setStories([...stories, formData]);
      setFormData({ name: "", message: "" });
    }
  };

  return (
    <div className="max-w-4xl mx-auto p-6">
      <h1 className="text-4xl font-extrabold mb-6 text-center text-indigo-700">
        Speak Out
      </h1>
      <p className="mb-8 text-center text-gray-700">
        Share your story or opinion about the importance of letting children choose their own path.
      </p>
      <form onSubmit={handleSubmit} className="mb-10 space-y-4">
        <div>
          <label htmlFor="name" className="block mb-1 font-semibold">
            Your Name
          </label>
          <input
            type="text"
            id="name"
            name="name"
            value={formData.name}
            onChange={handleChange}
            className="w-full p-2 border border-gray-300 rounded-md"
            required
          />
        </div>
        <div>
          <label htmlFor="message" className="block mb-1 font-semibold">
            Your Story / Opinion
          </label>
          <textarea
            id="message"
            name="message"
            value={formData.message}
            onChange={handleChange}
            rows="5"
            className="w-full p-2 border border-gray-300 rounded-md"
            required
          />
        </div>
        <button
          type="submit"
          className="bg-indigo-600 text-white px-6 py-3 rounded-md font-semibold hover:bg-indigo-700 transition"
        >
          Submit
        </button>
      </form>
      <section>
        <h2 className="text-2xl font-semibold mb-4 text-indigo-600">Shared Stories</h2>
        <div className="space-y-6">
          {stories.map((story, index) => (
            <div key={index} className="p-4 border border-gray-300 rounded-md bg-gray-50">
              <p className="italic mb-2">"{story.message}"</p>
              <p className="font-semibold text-right">- {story.name}</p>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
}
