import React from "react";

const articles = [
  {
    title: "Why Forcing Children Can Harm Their Growth",
    summary:
      "Explore the psychological and emotional impact of forcing children into decisions and how it can hinder their development.",
  },
  {
    title: "Encouraging Independence: Letting Children Choose",
    summary:
      "Learn strategies to support your child's independence and decision-making skills for a healthier parent-child relationship.",
  },
  {
    title: "The Importance of Listening to Your Child",
    summary:
      "Understand why active listening is crucial in parenting and how it fosters trust and open communication.",
  },
  {
    title: "Balancing Guidance and Freedom in Parenting",
    summary:
      "Discover how to provide guidance while respecting your child's choices to promote confidence and responsibility.",
  },
  {
    title: "How Technology Can Support Your Child's Learning",
    summary:
      "A look at how devices like PCs and smartphones can be valuable educational tools when used responsibly.",
  },
];

export default function Articles() {
  return (
    <div className="bg-gray-900 min-h-screen py-12">
      <div className="max-w-7xl mx-auto p-10 bg-gray-800 rounded-3xl shadow-2xl">
        <h1 className="text-4xl font-extrabold mb-10 text-center text-yellow-400 drop-shadow-lg">
          Helpful Articles
        </h1>
        <div className="space-y-8">
          {articles.map((article, index) => (
            <div
              key={index}
              className="bg-yellow-400 p-6 rounded-3xl shadow-lg text-gray-900 hover:shadow-2xl transition"
            >
              <h2 className="text-2xl font-bold mb-3">{article.title}</h2>
              <p className="text-lg leading-relaxed">{article.summary}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}
