import React from "react";

const stories = [
  {
    title: "The Pressure to Choose Science",
    content:
      "My parents insisted I take the science stream despite my passion for arts. It led to stress and poor performance because I wasn't interested in the subjects.",
  },
  {
    title: "Forced into Commerce",
    content:
      "I was forced to choose commerce because it was 'practical' and 'lucrative'. I struggled to keep up and felt lost, as my true interest was in literature and writing.",
  },
  {
    title: "Choosing Engineering Over My Dreams",
    content:
      "My parents wanted me to become an engineer, but I wanted to pursue music. The constant pressure made me anxious and unhappy, affecting my mental health.",
  },
  {
    title: "Breaking Free from Expectations",
    content:
      "After years of being pushed into a course I didn't like, I finally convinced my parents to let me follow my passion. It was the best decision for my happiness and growth.",
  },
  {
    title: "A Journey of Self-Discovery",
    content:
      "Growing up, I was always told to follow the traditional path of medicine or engineering. However, my heart was set on becoming a writer. The struggle to convince my parents was tough, but eventually, they saw my dedication. Today, I am a published author, and my story is a testament to the importance of following one's passion.",
  },
  {
    title: "From Rebellion to Understanding",
    content:
      "I rebelled against my parents' wishes to pursue a career in law and instead chose to study fine arts. The initial years were filled with conflict and misunderstanding. Over time, through open conversations and mutual respect, my parents came to appreciate my choices, and our relationship grew stronger.",
  },
];

export default function Stories() {
  return (
    <div className="p-10 max-w-5xl mx-auto">
      <h1 className="text-4xl font-bold mb-8 text-center text-indigo-700">
        Real Stories: The Impact of Forced Choices
      </h1>
      <div className="space-y-8">
        {stories.map((story, index) => (
          <div
            key={index}
            className="bg-indigo-100 p-6 rounded-lg shadow-md hover:shadow-lg transition"
          >
            <h2 className="text-2xl font-semibold mb-3 text-indigo-900">
              {story.title}
            </h2>
            <p className="text-indigo-800">{story.content}</p>
          </div>
        ))}
      </div>
    </div>
  );
}
